CREATE TABLE IF NOT EXISTS Images(imageId BIGINT PRIMARY KEY NOT NULL, uid BIGINT NOT NULL, title VARCHAR(255) NOT NULL,  content TEXT NOT NULL, creationTime BIGINT NOT NULL, url TEXT NOT NULL, s3bucket VARCHAR(255) NOT NULL, s3key VARCHAR(255) NOT NULL, width BIGINT NOT NULL, height BIGINT NOT NULL, basename VARCHAR(255) NOT NULL, extension VARCHAR(255) NOT NULL, filesize BIGINT NOT NULL, type BIGINT NOT NULL);
